$(document).ready(function(){$(".reject-confirm").click(function(){var a=$(this);vex.dialog.prompt({message:"Are you sure you want to reject this transaction?",placeholder:"Reject reason",callback:function(e){if(e){var t={id:a.data("id"),reject_reason:e};$.ajax({type:"POST",url:"/transaction/reject",dataType:"json",data:t,success:function(){location.reload()},error:function(){alert("An error has occured")}})}}})}),$(".delete-confirm").click(function(){var a=$(this);vex.dialog.confirm({message:"Are you sure you want to delete this transaction?",callback:function(e){e&&window.location.replace("/welcome/delete_transaction/"+a.data("id"))}})});$(".pb-date").datepicker({onRender:function(a){return a.valueOf()<now.valueOf()?"disabled":""}}).on("changeDate",function(){$(this).datepicker("hide");var a=$(this).val(),e=$(this);vex.dialog.open({message:"Are you sure you want to select paidback date as "+a+"?",callback:function(t){if(t){var o={id:e.data("id"),datepaidback:a};$.ajax({type:"POST",url:"/transaction/paidback",dataType:"json",data:o,success:function(a){500==a.status?vex.dialog.alert("Error - You have selected a date earlier than the transaction."):200==a.status&&location.reload()},error:function(){alert("An error has occured")}})}}})}).data("datepicker")});